/*
A Naive implementation of echo(1)
*/

var ProgramBase = require('programs/program_base');

function ProgramEcho() {
	this.name = "ProgramEcho";
	this.program_name = "echo";
}
ProgramEcho.prototype = new ProgramBase.ProgramBase();
ProgramEcho.prototype.constructor = ProgramEcho;

ProgramEcho.prototype.internal_run=function() {
	this.runtime.stdout.put_line(this.argv.join(" "));
	return 0;
}

module.exports = {
	"ProgramEcho" : ProgramEcho
};
