/*
A Naive implementation of false(1)
*/
"use strict";

var ProgramBase = require('programs/program_base');

module.exports = ProgramFalse;

function ProgramFalse() {
	this.name = "ProgramFalse";
	this.program_name = "false";
}
ProgramFalse.prototype = new ProgramBase();
ProgramFalse.prototype.constructor = ProgramFalse;

ProgramFalse.prototype.internal_run=function() {
	if (this.argv[0] === "--help") {
		this.show_help();
		this.exit(0);
	}

	if (this.argv[0] == "--version") {
		this.show_version();
		this.exit(0);
	}

	return 1;
}
