/*
A Naive implementation of true(1)
*/
"use strict";

var ProgramBase = require('programs/program_base');

module.exports = ProgramTrue;

function ProgramTrue() {
	this.name = "ProgramTrue";
	this.program_name = "true";
}
ProgramTrue.prototype = new ProgramBase();
ProgramTrue.prototype.constructor = ProgramTrue;

ProgramTrue.prototype.internal_run=function() {
	return 0;
}
